mapper.select:

select 

from ACTLGC
where APCODE = #{apcode,jdbcType=VARCHAR}
  and CURCDE = #{curcde,jdbcType=VARCHAR}
  and CUSIDT = #{cusidt,jdbcType=VARCHAR}
  and LEGPNY = #{legpny,jdbcType=DECIMAL}
  and NLEGPG = #{nlegpg,jdbcType=DECIMAL}
  and ORGIDT = #{orgidt,jdbcType=VARCHAR}
  and PAGLIN = #{paglin,jdbcType=DECIMAL}
  and SYSIDT = #{sysidt,jdbcType=VARCHAR}

mapper.delete:

delete from ACTLGC
where APCODE = #{apcode,jdbcType=VARCHAR}
  and CURCDE = #{curcde,jdbcType=VARCHAR}
  and CUSIDT = #{cusidt,jdbcType=VARCHAR}
  and LEGPNY = #{legpny,jdbcType=DECIMAL}
  and NLEGPG = #{nlegpg,jdbcType=DECIMAL}
  and ORGIDT = #{orgidt,jdbcType=VARCHAR}
  and PAGLIN = #{paglin,jdbcType=DECIMAL}
  and SYSIDT = #{sysidt,jdbcType=VARCHAR}
