mapper.select:

select 

from ACTVHH
where ERYDAT = #{erydat,jdbcType=TIMESTAMP}
  and ORGIDT = #{orgidt,jdbcType=VARCHAR}
  and SETSEQ = #{setseq,jdbcType=DECIMAL}
  and TLRNUM = #{tlrnum,jdbcType=VARCHAR}
  and VCHSET = #{vchset,jdbcType=DECIMAL}

mapper.delete:

delete from ACTVHH
where ERYDAT = #{erydat,jdbcType=TIMESTAMP}
  and ORGIDT = #{orgidt,jdbcType=VARCHAR}
  and SETSEQ = #{setseq,jdbcType=DECIMAL}
  and TLRNUM = #{tlrnum,jdbcType=VARCHAR}
  and VCHSET = #{vchset,jdbcType=DECIMAL}
